package Day2;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int f =100; 
		int g = 200;
		System.out.println("The value of f is"+f);
		System.out.println("The value of g is"+g);
		System.out.println("The sum of f and g is"+(f+g));
		
		byte by = 125;
		System.out.println("The value of by is "+by);
		
		short sh= 3535;
		System.out.println("The value of sh is "+sh);
		
		long lo= 123456675678l;
		System.out.println("The value of long is "+lo); // use literal for long
		
		//Decimal numbers
		
		float fl = 3.55f; //literal needed for float as well(only long and float requires literal)
		System.out.println("The value of float is "+fl);
		
		double dbl = 1223.456;
		System.out.println("The value of double is "+dbl);
		
		char grade= 'A'; // holds single quotes as it has single value
		System.out.println("The value of the character is"+grade);
		
		String name = "Manva"; //shud be in double quotes as it holds multiple values
		System.out.println("The value of string is"+name);
		
		
		

	}

}
