package Day3;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
	//	a=a+5;
		//Short hand operator
		a+=5;
		System.out.println(a);
		
		int b= 10;
		b-=5; //b=b-5
		System.out.println(b);
		
		int c=30;
	//	c= c*5;
		c*=5;
		System.out.println(c);
		
		int d= 4;
	//	d=d/2;
		d/=2;
		System.out.println(d); //2 reminder
		
		int e= 10;
		e=e%2;
		e%=2;
		System.out.println(e); //0 quotient
		
		//Just remember these are short hand operations
		
		
		
		
		
		
	}

}
