package Day4;

public class IfElseConditionDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int person_age = 18;
		if(person_age>18) {
			System.out.println("The person is eligible to vote");
		} else {
			System.out.println("The person is not eligible to vote");
		}  //The condition is always a Boolean expression, if it returns true, it means the block of statements in it will get executed, if the condition returns false, it means the control goes to next block.
	}

}
