package Day5;

public class DoWhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//printing 1 to 10
		int i=1;
		do {
			System.out.println(i);
		}while(i<=10);
		
		//printing 10 to 1
		int j=10;
		do {
			System.out.println(j);
			j--;
		}while(j>0);

	}

}
